mapper.select:
select user_id as userId, nickname, email, password, active, created, deleted
from user
where email = #{email}

select user_id as userId, nickname, email, password, active, created, deleted
from user
where user_id = #{userId}

select user_id as userId, nickname, email, password, active, created, deleted
from user
where nickname = #{nickname}

select user_role
from user_role
where user_id = #{userId}

select user_id as userId, nickname, email
from user
where nickname like concat('%', #{nickname},'%');
